library(ggplot2)
library(reshape2) 
library(Rmisc)
vector_performance <- read.csv(file="/mnt/bioinf/archive0/eigen_thread/results/eigenvectors_performance.csv", header=TRUE, check.names=FALSE, strip.white = TRUE, sep=",",na.strings= c("999", "NA", " ", ""))
distance_performance <- read.csv(file="/mnt/bioinf/archive0/eigen_thread/results/distance_performance.csv", header=TRUE, check.names=FALSE, strip.white = TRUE, sep=",",na.strings= c("999", "NA", " ", ""))

vector_performance$top<-as.factor(vector_performance$top)

distance_performance$top<-as.factor(distance_performance$top)
colnames(distance_performance)<-c("top", "distance", "class", "fold", "superf", "family")
v1<-ggplot(vector_performance, aes(x=vectors, y=class, group=top, color=top))+geom_line(size=0.8)+theme_set(theme_gray(base_size = 18)) + ggtitle("Class")+ylab("TPR")+xlab("No. of Eigen Vectors")+theme(plot.title= element_text(hjust=0.5))
v2<-ggplot(vector_performance, aes(x=vectors, y=fold, group=top, color=top))+geom_line(size=0.8)+theme_set(theme_gray(base_size = 18)) + ggtitle("Fold")+ylab("TPR")+xlab("No. of Eigen Vectors")+theme(plot.title= element_text(hjust=0.5))
v3<-ggplot(vector_performance, aes(x=vectors, y=superf, group=top, color=top))+geom_line(size=0.8)+theme_set(theme_gray(base_size = 18)) + ggtitle("Superfamily")+ylab("TPR")+xlab("No. of Eigen Vectors")+theme(plot.title= element_text(hjust=0.5))
multiplot(v1,v2,v3,cols=2)
d1<-ggplot(distance_performance, aes(x=distance, y=class, group=top, color=top))+geom_line(size=0.8)+theme_set(theme_gray(base_size = 18)) + ggtitle("Class")+ylab("TPR")+xlab("Distance")+theme(plot.title= element_text(hjust=0.5))
d2<-ggplot(distance_performance, aes(x=distance, y=fold, group=top, color=top))+geom_line(size=0.8)+theme_set(theme_gray(base_size = 18)) + ggtitle("Fold")+ylab("TPR")+xlab("Distance")+theme(plot.title= element_text(hjust=0.5))
d3<-ggplot(distance_performance, aes(x=distance, y=superf, group=top, color=top))+geom_line(size=0.8)+theme_set(theme_gray(base_size = 18)) + ggtitle("Superfamily")+ylab("TPR")+xlab("Distance")+theme(plot.title= element_text(hjust=0.5))
multiplot(d1,d2,d3,cols=2)